import React from 'react';

function AboutPage() {

  const text = `高校は進学校の通っていたため、高校卒業後はクラスの皆と同じように大学に通う予定(通える)と思っていたのですが、自分は親と元々仲が悪かったのですが、進路について相談したところ「大学なんて高いし高校卒業してからでも働けるから意味がない」と言われ学費を払う気ゼロでした。(2つ下の弟は高校卒業後に大学に進学してましたが...)。そんなこんなで卒業後は飲食店で働きながら学費をため、また自分が何をしたいのか明確にするためにいろんな勉強をし始めました。経済学であったり、物理学、数学、得意の英語をもっと伸ばそうとしたり。そんな中プログラミングも勉強し始めたところ初めて楽しいと感じ、どうせ働くのであれば自分の好きなことをしようと思いプログラミングに力を入れることに決めました。\n今ではバンタンテックフォードアカデミーに無事入学することができ、フロントエンジニアとして活躍したい、親父の年収を若いうちに越えたいという目標を掲げて毎日勉強しています。`

  return (
    <div className='about'>
      <div className='about-title'>
        <h2>Career</h2>
      </div>

      <div className='aboutme'>
        <h3>私について</h3>
        <h4>中村圭</h4>
        <hr />


      </div>

      <div className='about-my-career'>
        <h4>プログラミングについて学ぼうと思った経緯</h4>
        {text}
      </div>
    </div>
  )
}

export default AboutPage;
